mapper.select:
select
,f.name as office_name from fm_prisoner p left join sys_office f on p.office_id=f.id and f.enabled = 1 and f.deleted = 1 where p.deleted=3 and p.enabled_audit = 1
and ( name like concat('%',#{name},'%') or phone like concat('%',#{name},'%') )
and f.name like concat('%',#{officeName},'%')
and p.sex=#{sex}
and p.criminal_type like concat('%',#{criminalType},'%')
and p.orig_sentence like concat('%',#{origSentence},'%')
and p.corr_type like concat('%',#{corrType},'%')
and p.create_time >= #{timeStart}
and p.create_time <= #{timeEnd}
limit #{start},#{rows}

select count(p.id) from fm_prisoner p left join sys_office f on p.office_id=f.id and f.enabled = 1 and f.deleted = 1 where p.deleted=3 and p.enabled_audit =1
and ( f.name like concat('%',#{name},'%') or p.phone like concat('%',#{name},'%') )
and p.name like concat('%',#{officeName},'%')
and p.sex=#{sex}
and p.criminal_type like concat('%',#{criminalType},'%')
and p.orig_sentence like concat('%',#{origSentence},'%')
and p.corr_type like concat('%',#{corrType},'%')
and p.create_time >= #{timeStart}
and p.create_time <= #{timeEnd}

mapper.update:
update fm_prisoner set			
	deleted=3			
where id = #{id}

update fm_prisoner set			
	deleted=1			
where id = #{id}
